SqlQuery.Text:
insert into template.city(
  title,
  archive
)
values
  (
  {Title},
  {Archive}
  );

update template.city
set
  title = {Title},
  archive = {Archive}
where
  city_id = {CityId};

WITH _filter AS ( SELECT CASE WHEN {Filter} = 'Today' THEN CURRENT_DATE::timestamp WHEN {Filter} = 'CurrentWeek' THEN date_trunc('week', CURRENT_DATE::timestamp) WHEN {Filter} = 'Fortnight' THEN date_trunc('week', CURRENT_DATE::timestamp) - interval '7d' WHEN {Filter} = 'Period' THEN {StartDate}::timestamp END AS start_date,
CASE WHEN {Filter} = 'Today' THEN CURRENT_DATE + interval '1d - 1s' WHEN {Filter} = 'CurrentWeek' THEN date_trunc('week', CURRENT_DATE::timestamp) + interval '7d - 1s' WHEN {Filter} = 'Fortnight' THEN date_trunc('week', CURRENT_DATE::timestamp) + interval '7d - 1s' WHEN {Filter} = 'Period' THEN {EndDate}::timestamp + interval '1d - 1s' END AS end_date ) select ord.order_id as "OrderId", ord.client_id as "ClientId", ord.order_number as "OrderNumber", ord.order_date as "OrderDate", ord.description as "OrderDescription", client.city_id as "CityId"
from template.order ord left join template.client on client.client_id = ord.client_id left join _filter on true where not ord.deleted and ord.order_date between convert_date_filter(_filter.start_date) and convert_date_filter(_filter.end_date);

update  template.order set
  client_id = {ClientId},
  order_number = {OrderNumber},
  order_date = {OrderDate}, 
  description = {OrderDescription}
where order_id = {OrderId};

INSERT INTO template.unit (
  title
)
VALUES (
  {Title}
)
RETURNING unit_id;

UPDATE template.unit
SET
  title = {Title}
WHERE
  unit_id = {UnitId};

	INSERT INTO template.material_category (
	  title,
      parent_material_category_id
	)
	VALUES (
	  {Title},
      {ParentMaterialCategoryId}
	)
	RETURNING material_category_id;

UPDATE template.material_category
SET
  title = {Title}, parent_material_category_id = {ParentMaterialCategoryId}
WHERE
  material_category_id = {MaterialCategoryId};
