UPDATE template.city
SET
  title = {Title},
  archive = {Archive}
WHERE
  city_id = {CityId};

UPDATE template.client
SET
  archive = {Archive}
WHERE
  client_id = {ClientId};

WITH _filter AS ( SELECT CASE WHEN {Filter} = 'Today' THEN CURRENT_DATE::timestamp WHEN {Filter} = 'CurrentWeek' THEN date_trunc('week', CURRENT_DATE::timestamp) WHEN {Filter} = 'Fortnight' THEN date_trunc('week', CURRENT_DATE::timestamp) - interval '7d' WHEN {Filter} = 'Period' THEN {StartDate}::timestamp END AS start_date,
CASE WHEN {Filter} = 'Today' THEN CURRENT_DATE + interval '1d - 1s' WHEN {Filter} = 'CurrentWeek' THEN date_trunc('week', CURRENT_DATE::timestamp) + interval '7d - 1s' WHEN {Filter} = 'Fortnight' THEN date_trunc('week', CURRENT_DATE::timestamp) + interval '7d - 1s' WHEN {Filter} = 'Period' THEN {EndDate}::timestamp + interval '1d - 1s' END AS end_date ), _order AS ( SELECT O.order_id, O.client_id, C.city_id, O.order_number, O.order_date FROM template.order O LEFT JOIN template.client C USING(client_id) LEFT JOIN _filter ON true WHERE O.added AND NOT O.deleted AND O.order_date BETWEEN convert_date_filter(_filter.start_date) AND convert_date_filter(_filter.end_date) ), _order_position AS ( SELECT O.order_id, SUM(OP.quantity * OP.unit_price) AS total_cost FROM _order O JOIN template.order_position OP USING(order_id) LEFT JOIN template.material M USING(material_id) GROUP BY O.order_id ), _payment AS ( SELECT O.order_id, SUM(C.summ) AS total_payment_summ FROM _order O JOIN template.order_payment OP USING(order_id) JOIN template.cash C USING(cash_id) WHERE NOT C.deleted GROUP BY O.order_id )
SELECT O.order_id AS "OrderId", O.client_id AS "ClientId", O.city_id AS "CityId", O.order_number AS "OrderNumber", O.order_date AS "OrderDate", COALESCE(OP.total_cost, 0) AS "TotalOrderCost", COALESCE(P.total_payment_summ, 0) AS "TotalPaymentSumm", COALESCE(OP.total_cost, 0) - COALESCE(P.total_payment_summ, 0) AS "TotalDebt" FROM _order O LEFT JOIN _order_position OP USING(order_id) LEFT JOIN _payment P USING(order_id) ORDER BY O.order_number ASC;

INSERT INTO template.order (added)
VALUES (false)
RETURNING order_id;

